onChange.script:
root = self.getChild("root")
root.setComponentsDb(currentValue.value)
if currentValue.value:
	self.custom.toUpdateDelete = True
else:
	self.custom.toUpdateDelete = False

config.script:
applicantStatusId = self.view.params.applicantStatusId
root = self.view.getChild("root")
lbl = self.getSibling("Label")

validationMsg = root.getValidationMessage()
#ystem.perspective.print(('validationMsg',validationMsg,self.view.custom.toUpdateDelete))
if validationMsg:
	lbl.showMessage(validationMsg)
	return

try:
	if self.view.custom.toUpdateDelete:
		applicantStatusIdUpdated = root.updateDb(applicantStatusId)
	else:
		applicantStatusIdInserted = root.insertDb()
except:
	lbl.showMessage("Something went wrong while saving data. Please try again.")
	raise

payload = {'employerId':self.view.params.employerId}

system.perspective.sendMessage('mhByCondition',payload)
root.clearInputs()

applicantStatusId = self.view.params.applicantStatusId
root = self.view.getChild("root")
lbl = self.getSibling("Label")

try:
	applicantStatusIdDeleted = root.retireDb(applicantStatusId)
except:
	lbl.showMessage("Something went wrong while retiring data. Please try again.")

payload = {'employerId':self.view.params.employerId}
system.perspective.sendMessage('mhByCondition',payload)
