config.script:
root = self.parent.parent
tbl = root.getChild("Table")
emvName = self.getSibling("EmployerName")
btnCreateEdit = root.getChild("FlexContainer").getChild("ButtonCreateEdit")

payload = {'employerName': emvName.props.params.textValue, 'street': self.props.params.textValue}

tbl.doByCondition(payload)	

if self.props.params.textValue and emvName.props.params.textValue:
	btnCreateEdit.meta.visible = True
else:
	btnCreateEdit.meta.visible = False

root = self.view.getChild("root")
emvName = root.getChild("FlexContainerSearch").getChild("EmployerName")
emvStreet = root.getChild("FlexContainerSearch").getChild("Street")
tbl = self.parent.parent.getChild("Table")

_employerId = self.view.custom.employerId
system.perspective.print(('click id: ',_employerId))


if isinstance(_employerId, (int, long)) and _employerId >= 0:
	params = {'employerInfo': {	'employerId': _employerId}}
else:
	_employerName =  emvName.props.params.textValue
	_street = emvStreet.props.params.textValue
	params = {'employerInfo': {
		'employerName': _employerName,
		'street': _street}
		}


system.perspective.print(('params: ',params))
root.clearAll()	
system.perspective.openDock("RegisterEmployer", params = params)

