	colNames = system.dataset.getColumnHeaders(dataset)

	_columns = []
	for col in colNames:
		# This conversion json_>dict create actually deepcopy
		pyColumn = system.util.jsonDecode(jsonDefaultColumn)

		# Field must not contain spaces. Spaces are already removed for props.data on same way.
		pyColumn['field'] = col

		_dictCol = None
