customMethods.script:
# get dates / times
startTime = self.parent.parent.getChild("TimeRange").props.params.startTime
startHour = system.date.getHour24(startTime)
startMinute = system.date.getMinute(startTime)
startSecond = system.date.getSecond(startTime)

endTime = self.parent.parent.getChild("TimeRange").props.params.endTime
endHour = system.date.getHour24(endTime)
endMinute = system.date.getMinute(endTime)
endSecond = system.date.getSecond(endTime)

startDate = self.getSibling("StartDate").props.params.inputDate
endDate = self.getSibling("EndDate").props.params.inputDate

startDateTime = system.date.setTime(startDate,startHour,startMinute,startSecond)
endDateTime = system.date.setTime(endDate,endHour,endMinute,endSecond)

if self.parent.parent.getChild("RepeatType").props.params.SelectedValue == 'once' :
	self.props.text = "Events : " + str(1)

if self.parent.parent.getChild("RepeatType").props.params.SelectedValue == 'hourly':
	numberOfHours = system.date.hoursBetween(startDateTime,endDateTime)
	numberOfHours = 0 if numberOfHours <0 else numberOfHours
	self.props.text = "Events : " + str(numberOfHours)

if self.parent.parent.getChild("RepeatType").props.params.SelectedValue == 'daily':
	numberOfDays = system.date.daysBetween(startDateTime,endDateTime)
	self.props.text = "Events : " + str(numberOfDays)


if self.parent.parent.getChild("RepeatType").props.params.SelectedValue == 'weekly':
	# check selected Days
	daysSelected = []
	if self.parent.parent.getChild("DaysOfWeek").props.params.Sunday : daysSelected.append(1)
	if self.parent.parent.getChild("DaysOfWeek").props.params.Monday : daysSelected.append(2)
	if self.parent.parent.getChild("DaysOfWeek").props.params.Tuesday : daysSelected.append(3)
	if self.parent.parent.getChild("DaysOfWeek").props.params.Wednesday : daysSelected.append(4)
	if self.parent.parent.getChild("DaysOfWeek").props.params.Thursday : daysSelected.append(5)
	if self.parent.parent.getChild("DaysOfWeek").props.params.Friday : daysSelected.append(6)
	if self.parent.parent.getChild("DaysOfWeek").props.params.Saturday : daysSelected.append(7)

	# get number of weeks
	numberOfWeeks = system.date.weeksBetween(startDate,endDate)
	date = startDateTime

	numberOfEvents = 0
	# loop trough weeks
	for x in range(numberOfWeeks +2) :
		for y in daysSelected:
			dayNumber = y - system.date.getDayOfWeek(date)

			tempDate = system.date.addDays(date,dayNumber)
			tempDateEnd = system.date.setTime(date,endHour,endMinute,endSecond)

			if system.date.isBefore(tempDateEnd,tempDate) :
				tempDateEnd = system.date.addDays(tempDateEnd,1)

			if(system.date.isAfter(tempDate,startDateTime) and system.date.isBefore(tempDate,endDateTime)) or tempDate == startDateTime :
				if (system.date.isAfter(tempDateEnd,startDateTime) and system.date.isBefore(tempDateEnd,endDateTime)) or tempDateEnd == endDateTime:
					numberOfEvents += 1
		date = system.date.addWeeks(date,1)
	self.props.text = "Events : " + str(numberOfEvents)

if self.parent.parent.getChild("RepeatType").props.params.SelectedValue == 'monthly':
	numberOfMonths = system.date.monthsBetween(startDateTime,endDateTime)
	self.props.text = "Events : " + str(numberOfMonths)

if self.parent.parent.getChild("RepeatType").props.params.SelectedValue == 'yearly':
		numberOfYears = system.date.yearsBetween(startDateTime,endDateTime)
		self.props.text = "Events : " + str(numberOfYears)
