pyBase = system.util.jsonDecode(self.view.params.jsonBase)
pyOut = system.util.jsonDecode(self.view.params.jsonOut) 

if not(isinstance(pyOut, dict)):
	pyOut = {}

# Add to jsonOut to node 'wherePlus' condition 'cnd' which is specified in jsonBase
# 'cnd' is condition with full column name: "[schema].[table].[column] = ?"
# or if nothing is selected - special case "always True" like -1 = ? (and arg is -1). 
# For example: "cnd":"[lodging].[Room].[id] = ?"
# example for jsonOut: {"wherePlus":[{"arg":7,"cnd":"[lodging].[Room].[id] = ?"}]}
# example for jsonOut: {"wherePlus":[{"arg": -1,"cnd":"-1 = ?"}]}

strDate = str(currentValue.value)

formatedDate = '-'.join([strDate[:4],strDate[4:6],strDate[6:]]) +' 12:00:00'

if currentValue.value > 0:
    pyOut["wherePlus"] = [ {'arg':formatedDate, 'cnd': pyBase['cnd'], 'type':'datetime'} ]
else:
	pyOut["wherePlus"] = [ {'arg': currentValue.value, 'cnd': '{} = ?'.format(currentValue.value)}]

self.view.params.jsonOut = system.util.jsonEncode(pyOut)

#system.perspective.print(('Dropdown  onvaluechanged jsonOut', self.view.params.jsonOut ))

