if not isinstance(pyIn,dict):
	return

wherePlus = pyIn.get('wherePlus', None)
if 'prepQuery' in pyBase:

	# Return if "wherePlus": None or "wherePlus" doesn't exist inside in jsonIn. 
	# Special case for query with no parameters is to set "wherePlus":[]
	if wherePlus is None:
		return

	org = """
	args = [x['arg'] for x in pyIn['wherePlus']]

	query = (pyBase['prepQuery']).get('query', None)

	if isinstance(query, str):
		drp = self.getChild("Dropdown")

		# If args[0] (usually it is id column in db table) is not positive integer avoid query execution, list of option is None
		if isinstance(args,list) and len(args) == 1 and args[0] <= 0:
			drp.props.options = None
		else:
			drp.props.options = system.db.runPrepQuery(query,args,'RITIOps') 

		drp.props.value = -1	"""	




	system.perspective.print(("pyIn['wherePlus']: ",pyIn['wherePlus']))


	#[{u'type': 'datetime', u'arg': '2020-11-19 12:00:00', u'cnd': '[lodging].[Reservation].[reservationStart] >= ?'}]
	queryArgs = [ system.date.parse(x['arg']) if x.get('type', '') == 'datetime' else x['arg'] for x in pyIn['wherePlus'] ]


	query = (pyBase['prepQuery']).get('query', None)

	if isinstance(query, str):
		drp = self.getChild("Dropdown")

		# If args[0] (usually it is id column in db table) is not positive integer avoid query execution, list of option is None
		if isinstance(queryArgs,list) and len(queryArgs) == 1 and queryArgs[0] <= 0:
