lstCnd = [x.strip() for x in wherePlus.split(' AND ')]

lstOpArg = [(x.strip()).split('] ')[1] for x in lstCnd]

system.perspective.print(lstCnd)
system.perspective.print(lstOpArg)


auxList = []
i= 0
for x in lstOpArg:
	auxDict = {}
	#get string reverse
	xRev = x[::-1]


	if xRev[0] == '"':
		firstQuoteFromBack = (xRev[1:]).find('"')
		strArgRev = xRev[1:firstQuoteFromBack + 1]
		strArg = strArgRev[::-1]

		try:
			system.date.parse(strArg,'yyyy-MM-dd HH:mm:ss') 
			auxDict['arg'] = strArg
			auxDict['type'] = 'datetime'
		except:
			auxDict['arg'] = strArg

		toDel = len(strArg) + 2
		auxDict['cnd'] = lstCnd[i][:-toDel] + '?'

		system.perspective.print(auxDict)
	else:
		firstBlankFromBack = xRev.find(' ')
		strArgRev = xRev[:firstBlankFromBack]
		strArg = strArgRev[::-1]	
		toDel = len(strArg)	
		auxDict['cnd'] = lstCnd[i][:-toDel] + '?'

		if strArg == 'true':
			arg = True
		elif strArg == 'false':
			arg = False
		elif strArg == 'null':
			arg = None
		else:
			try:
				arg = int(strArg)
			except:
				try:
					arg = long(strArg)
				except:				
					try:
						arg = float(strArg)
					except:				
						raise ValueError("Unknown arg type!")
		auxDict['arg'] = arg
	auxList.append(auxDict)

	system.perspective.print(auxDict)		
	i += 1	

system.perspective.print(auxList)



return system.util.jsonEncode(auxList)


