if value is None:
	return False
elif value == False:
	return False
else:
	return True

data = self.custom.categoryData
try:
	id = data.getValueAt(value, 'id')
	name = data.getValueAt(value, 'name')
	canAdd = data.getValueAt(value, 'canAdd')
	canDeduct = data.getValueAt(value, 'canDeduct')
except:
	return {'id':None, 
			'name': None,
			'canAdd': False,
