params = {
	'col': self.getSibling("Column").custom.SelectedColumn,
	'bin': self.getSibling("Bin").custom.SelectedBin
}
res = system.db.runNamedQuery('Participants/Storage Bins/GetSpecificBinStatus', params)
if res.getRowCount() == 0:
	return 'Available'
action = system.date.getDayOfMonth(res.getValueAt(0, 0))
if action == 1:
	if system.date.daysBetween(system.date.now(),  res.getValueAt(0, 0)) > 30:
		return 'Expired'
	else:
		return 'In-Use'

return 'Available'

params = {'participantId': self.view.params.Selected_Participant_Id}
name = system.db.runNamedQuery('Participants/Storage Bins/GetParticipantName', params)
if name.getRowCount() == 0:
	return ''
fname = name.getValueAt(0, 0)
mname = name.getValueAt(0, 1)
lname = name.getValueAt(0, 2)
dname = ''
if fname != None:
	dname += fname + ' '
if mname != None:
	dname += mname + ' '
if lname != None:
	dname += lname + ' '

return dname
