onChange.script:
import traceback
try:
	if currentValue.value > -1:
		system.perspective.print('Delete Participant')
		participant_id = currentValue.value
		system.perspective.print(participant_id)
		#Remove the participant from the ID list
		for row in range(len(self.custom.edit_data.selected_id_list)):
			if self.custom.edit_data.selected_id_list[row] == participant_id:
				self.custom.edit_data.selected_id_list.pop(row)
				#Find the table row and delete from the table
				for id_row in range(len(self.custom.edit_data.selected_participants)):
					if self.custom.edit_data.selected_participants[id_row].participant_id == participant_id:
						self.custom.edit_data.selected_participants.pop(id_row)
						break
				break
				system.perspective.print('Participant removed from selection list')
	#Reset the value to -1
	self.custom.func_delete_participant = -1
except:
	logger = system.util.getLogger("Participants ServicesBatchEdit view.custom.func_delete_participant")
	msg = traceback.format_exc()
	logger.error(msg)
