config.script:
root = self.getChild("root")

try:
	payload = {}
	root.doByPayload(payload)
except:
	root.cleanKpis()

onChange.script:
root = self.getChild("root")
if currentValue.value is True:
	payload = {}
	payload['providerId'] = 30
	root.doByPayload(payload)

else:
	root.cleanKpis()

root = self.getChild("root")

if currentValue.value is not None:
	payload = {}
	payload['providerId'] = currentValue.value
	root.doByPayload(payload)
else:
	root.cleanKpis()
