onChange.script:
if self.custom._isDev:
	if currentValue.value is True:
		payload = {}

		payload['providerId'] = 9

		system.perspective.sendMessage('mhByCondition', payload = payload)
		self.custom.jsonLastPayload = system.util.jsonEncode(payload)

	else:
		#self.props.data = system.dataset.clearDataset(self.props.data)
		self.props.data = [{key: None for key in self.props.data[0]}]
		self.custom.jsonLastPayload = None

customMethods.script:
	jsonDefaultColumn = """
  {
	    "field": "",
	    "visible": true,
	    "editable": false,
	    "render": "auto",
	    "justify": "left",
	    "align": "center",
	    "resizable": true,
	    "sortable": true,
	    "sort": "none",
	    "viewPath": "",
	    "viewParams": {},
	    "boolean": "checkbox",
	    "number": "value",
	    "progressBar": {
	      "max": 100,
	      "bar": {
	        "color": "#62A3F6",
	        "linecap": "round",
	        "width": 5
	      },
	      "track": {
	        "color": "#DADADA",
	        "linecap": "round",
	        "width": 2
	      },
	      "value": {
	        "color": "#7D7D7D",
	        "show": true
	      }
	    },
	    "toggleSwitch": {
	      "color": {
	        "selected": "#2196F3",
	        "unselected": "#FFFFFF"
	      }
	    },
	    "numberFormat": "0,0.##",
	    "dateFormat": "MM/DD/YYYY",
	    "width": "",
	    "strictWidth": false,
	    "header": {
	      "title": "",
	      "justify": "left",
	      "align": "center",
	      "style": {
	        "classes": "Table/Column_Header"
	      }
	    },
	    "footer": {
	      "title": "",
	      "justify": "left",
	      "align": "center",
	      "style": {
	        "classes": ""
	      }
	    },
	    "style": {
	      "classes": "Table/Column_Normal"
	    }
	  },	
	"""

	columnsForced = [
	  {
	    "field": "timeCreated",
	    "width": 120,
	    "title": "Date"
	  },
	  {
	    "field": "note",
	    "width": None,
	    "title": "Note"
	  },
	]

	colNames = system.dataset.getColumnHeaders(dataset)

	_columns = []
	for col in colNames:
		# This conversion json_>dict create actually deepcopy
		pyColumn = system.util.jsonDecode(jsonDefaultColumn)

		# Note: Field must contain only letters and numbers.
		pyColumn['field'] = col

		_dictCol = None
		for item in columnsForced:
			if item['field'] == pyColumn['field']:
				_dictCol = item.copy() 

		#ystem.perspective.print((_dictCol))

		pyColumn['header']['title'] = _dictCol['title'] if _dictCol else ''

		if _dictCol and _dictCol.get('width'):
			pyColumn['width'] = _dictCol['width']
			pyColumn['strictWidth'] = True

		if col in ['id']:
			pyColumn['visible'] = False

		_columns.append(pyColumn)

	#ystem.perspective.print(("_columns: ",_columns))

	return _columns
