onChange.script:
if currentValue.value is True:
	self.setOptions()
elif currentValue.value is False:
	self.props.params.options = []

customMethods.script:
emvHeader = self.getChild("emvHeader")

_dictComp = self.getDictComponents()

_dictComp['providerTypeId'].setOptions()

if providerInfo['providerId'] > -1:
	emvHeader.props.params.text = 'Edit Provider'

	_providerId = providerInfo['providerId']
	pyds = self.getProviderDb(_providerId)
	system.perspective.print(('pyds:', [list(x) for x in pyds]))

	for key in _dictComp:

		if	key == 'timeRetired': 
			# If value 'timeRetired' is None provider is active
			_dictComp[key].props.selected = False if pyds.rowCount > 0 and pyds[0][key] else True
		elif 'inputText' in _dictComp[key].props.params:
			_dictComp[key].props.params.inputText = pyds[0][key] if pyds.rowCount > 0 else ''
		elif 'selectedValue' in _dictComp[key].props.params:
			_dictComp[key].props.params.selectedValue = pyds[0][key] if pyds.rowCount > 0 else None
		else:
			pass
else:
	emvHeader.props.params.text = 'Register Provider'

	for key in _dictComp:
		if	key == 'providerName': 
			_dictComp[key].props.params.inputText = providerInfo['providerName']
		elif key == 'street': 
				_dictComp[key].props.params.inputText = providerInfo['street']
		elif	key == 'timeRetired': 
			# If value 'timeRetired' is None provider is active
			_dictComp[key].props.selected = True
		elif 'inputText' in _dictComp[key].props.params:
			_dictComp[key].props.params.inputText = ''
		elif 'selectedValue' in _dictComp[key].props.params:
			_dictComp[key].props.params.selectedValue = None
		else:
			pass
