customMethods.script:
emvHeader = self.getChild("Header_EmbeddedView")

try:
	facilityId = long(facilityId)
except:
	emvHeader.props.params.Text = 'No data for facilityId {}!'.format(facilityId)
else:

	queryShelterInfo = """
	SELECT [facilityName]
		FROM [RitiOps].[lodging].[Facility] 
	WHERE [id] = ?"""

	result = system.db.runPrepQuery(queryShelterInfo,[facilityId],'RITIOps')

	if result.rowCount == 1:
		emvHeader.props.params.Text = result.getValueAt(0, 'facilityName')
	else:
		emvHeader.props.params.Text = 'No data for facilityId {}!'.format(facilityId)
