customMethods.script:
drp = self.getChild("Dropdown")

wherePlusIn = self.view.params.wherePlusIn
query = self.view.params.query
useCndIn = self.view.params.useCndIn

try:
	queryCore = query.get('queryCore')
except:
	queryCore = None

if queryCore is None:
	drp.props.options = None
	return

# wherePlusIn value null could be used if embeded view is dependent (wait for some wherePlusIn) but we want 
# to avoid prepQuery execution on start up. If we want to execute on start up then wherePlusIn is empty array (like [])
if wherePlusIn is None:
	drp.props.options = None
	return

try:	
	orderby  = query.get('orderby') 	
except:
	orderBy = None		


if isinstance(wherePlusIn, list) or type(wherePlusIn).__name__ == "PropertyTreeScriptWrapper$ArrayWrapper":
	import extend.query 
	prepQuery, args = extend.query.getPrepQueryArgs(queryCore, wherePlus=wherePlusIn, orderby=orderby, useCnd = useCndIn)
else:
	raise TypeError("wherePlusIn type is not correct -> type(wherePlusIn).__name__: {}".format(type(wherePlusIn).__name__))

# Check if useCndIn  is False and args[0] == -1 (usually it is id column in db table) to avoid query execution, list of option is None
if not(useCndIn) and isinstance(args,list) and len(args) == 1 and args[0] == -1:
	drp.props.options = None
elif prepQuery is None:
	drp.props.options = None
else:
	#ystem.perspective.print(('\n ##before exec###',prepQuery, args))
	drp.props.options = system.db.runPrepQuery(prepQuery,args,'RITIOps') 

drp.props.value = -1
