config.script:
for item in range(len(self.props.data)):
if item == event.row:
	self.props.data[item].style.classes = 'Table/Row_Select_Detail'
else:
	self.props.data[item].style.classes = 'Table/Cell_Detail'

if self.view.custom.selectAll == 0:
	SelectedRow = event.selectedRow
	PriorSelected = self.view.custom.priorSelected
	AllData = self.props.data

	if SelectedRow > -1:
		AllData[SelectedRow].style.classes = 'Table/Row_Select_Detail'

	CurrentIds = []
	for Row in event.data:
		NewRow = Row['rowId']
		CurrentIds.append(NewRow)

	for PriorRow in PriorSelected:
		if AllData:
			if PriorRow not in CurrentIds:
				AllData[PriorRow].style.classes = 'Table/Cell_Detail'
			else:
				AllData[SelectedRow].style.classes = 'Table/Row_Select_Detail'

	self.view.custom.priorSelected = CurrentIds
else:
	pass

