if self.getChild("EmbeddedView").props.params.volunteer:
	if len(self.getChild("Volunteers").props.selection.data) > 0:
		name = self.getChild("Volunteers").props.selection.data[0].firstName + " " + self.getChild("Volunteers").props.selection.data[0].lastName
